import React from 'react';
import {Card,CardBody} from 'reactstrap';
import {Link} from 'react-router-dom';
import tours from '../assets/data/tours';


// import "./tour-card.css";



const TourCard = (tours) => {

    // const {id, title, city, photo, price, featured, avgRating, reviews}
  return (
    
    <>
    <div className="tour__card">
        <h3>{tours.title}</h3>
        <Card>
            <div className="tour__img">
                <img src={tours.photo} alt="tour-img" />
                <span>Featured</span>
            </div>
            <CardBody>
            <div className="card__top d-flex align-items-center justify-content-between">
                <span className="tour__location d-flex align-items-center  gap-1">
                <i class="ri-map-pin-line"></i> {tours.city}
                </span>

                <span className="tour__rating d-flex align-items-center  gap-1">
                <i class="ri-star-fill"></i> {tours.avgRating}{" "}
                <span>({tours.reviews})</span>
                </span>
            </div>
            <h5 className="tour__title"><Link to={'/tours/${id}'}>{tours.title}</Link></h5>
            <div className="card__bottom d-flex align-items-center justify-content-between mt-3">
                <h5>
                    ${tours.price}
                    <span>
                        /per person
                    </span>
                </h5>
                <button className="btn booking__btn">
                <Link to={'/tours/${id}'}>Book Now</Link>
                </button>
            </div>
        </CardBody>
        </Card>
    </div>
    </>
  )
}

export default TourCard;
